mapper.select:
        select * from menu


				and id = #{param.id}


				and name like concat('%',#{param.name},'%')


				and seq = #{param.seq}


				and node_id = #{param.nodeId}


				and url like concat('%',#{param.url},'%')


				and external_url like concat('%',#{param.externalUrl},'%')


				and id in (select menu_id from menu_permissions_merge where permissions_id in

					#{item}
