mapper.select:
        select * from issue_link_person


				and id = #{param.id}


				and name like concat('%',#{param.name},'%')


				and id_card like concat('%',#{param.idCard},'%')


				and phone like concat('%',#{param.phone},'%')


				and remark like concat('%',#{param.remark},'%')


				and issue_id = #{param.issueId}


				and is_delete = #{param.isDelete}
